import XCTest
@testable import Advent2022

final class Advent2022Puzzle6Tests: XCTestCase {
    func testPuzzle6() {
        XCTAssertEqual(Puzzle6.solve("mjqjpqmgbljsphdztnvjfqwrcgsmlb", distinctCount: 4), 7)
        XCTAssertEqual(Puzzle6.solve("bvwbjplbgvbhsrlpgdmjqwftvncz", distinctCount: 4), 5)
        XCTAssertEqual(Puzzle6.solve("nppdvjthqldpwncqszvftbrmjlhg", distinctCount: 4), 6)
        XCTAssertEqual(Puzzle6.solve("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", distinctCount: 4), 10)
        XCTAssertEqual(Puzzle6.solve("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw", distinctCount: 4), 11)
        XCTAssertEqual(Puzzle6.solve("", distinctCount: 4), 1175)
    }

    func testPuzzlePart2() {
        XCTAssertEqual(Puzzle6.solve("mjqjpqmgbljsphdztnvjfqwrcgsmlb", distinctCount: 14), 19)
        XCTAssertEqual(Puzzle6.solve("bvwbjplbgvbhsrlpgdmjqwftvncz", distinctCount: 14), 23)
        XCTAssertEqual(Puzzle6.solve("nppdvjthqldpwncqszvftbrmjlhg", distinctCount: 14), 23)
        XCTAssertEqual(Puzzle6.solve("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", distinctCount: 14), 29)
        XCTAssertEqual(Puzzle6.solve("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw", distinctCount: 14), 26)
        XCTAssertEqual(Puzzle6.solve("", distinctCount: 14), 3217)
    }
}
